DROP TABLE IF EXISTS `cms_content`;
CREATE TABLE `cms_content`  (
  `content_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `site_id` bigint(20) NOT NULL COMMENT '所属站点ID',
  `catalog_id` bigint(20) NOT NULL COMMENT '所属栏目ID',
